
public class PrimeraClase {
	/*Todo programa java debe estar dentro de una clase*/	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Hola hola hola");
		
		//1. Tipos de datos primitivos en Java
		
			//Enteros
		
		//Integer: 4 bytes de espacio en la memoria
		int entero = 1;
		//Short: 2 bytes de espacio en la memoria
		short corto = 1;
		//Long: 8 bytes de espacio en la memoria
		long largo = 1;
		//Byte: 1 byte de espacio en la memoria
		byte unByte = 1;
		
			//Flotantes: decimales
		
		//Float: 4 bytes de espacio en la memoria. Si se inicia con un valor, se debe colocar una "F/f" al final del valor.
		float decimalCorto = (float) 1.1;
		//Double: 8 bytes de espacio en la memoria
		double decimalLargo = 1.1;
		
			//Char: representaci√≥n de caracteres, deben ir entre comillas
		
		char ej1 = 'a';
		char ej2 = 'b';
		char ej3 = 'c';
		
			//Boolean: True o False, 2 byte de espacio en la memoria
		
		boolean verdad = true; //true equivale a 1 
		boolean falso = false; //false equivale a 0			

	}

}
